import './App.scss';
import { useEffect, useState } from 'react';
import { ApiQuote } from './types/quotes.types';
import Quotes from './Quotes';
function App() {
const [quotes,setQuotes] = useState<ApiQuote[]>([]);
const [search,setSearch] = useState<string>("");
const [allquotes, setAllquotes] = useState<ApiQuote[]>([]);
useEffect(()=>{
  setQuotes((allquotes)=>{
    return allquotes.filter((quote)=> 
    quote.content.includes(search))
  })

},[search]);

 useEffect(()=>{
setQuotes(allquotes)
},[allquotes])

const onSearchChangeHandler = (e:any) => {
    setSearch(e.target.value);
  }

  useEffect(()=>{
    fetch("https://api.quotable.io/quotes/random?limit=3")
    .then((response) => response.json())
    .then((data:ApiQuote[])=>{
      setQuotes(data);
    });
  },[]);

  return (
    <>
    Hello
    {/* {JSON.stringify(quotes)} */}
    <input type="text" onChange={onSearchChangeHandler}/>
    {quotes.map((quote)=>{
      return <Quotes key = {quote._id} quote=
      {quote}/>
    })}
    </>
  );
}

export default App;
