import './App.scss';
import { useEffect, useState } from 'react';
import { ApiQuote } from './types/quotes.types';
import Quotes from './Quotes';
function App() {
const [quotes,setQuotes] = useState<ApiQuote[]>([]);
const [search,setSearch] = useState<string>("");

useEffect(()=>{
  setQuotes(quotes.filter((quote)=> 
  quote.content.includes(search)))

},[search]);

const onSearchChangeHandler = (e:React.
  ChangeEvent<HTMLInputElement>) => {
    setSearch(e.target.value);
  }

  useEffect(()=>{
    fetch("https://api.quotable.io/quotes/random?limit=3")
    .then((response) => response.json())
    .then((data:ApiQuote[])=>{
      setQuotes(data);
    });
  },[]);

  return (
    <>
    Hello
    {/* {JSON.stringify(quotes)} */}
    <input type="text" onChange={onSearchChangeHandler}/>
    {quotes.map((quote)=>{
      return <Quotes key = {quote._id} quote=
      {quote}/>
    })}
    </>
  );
}

export default App;
