import './App.scss';
import { useEffect, useState } from 'react';
import { ApiQuote } from './types/quotes.types';
import Quotes from './Quotes';

function App() {

const [quotes,setQuotes] = useState<ApiQuote[]>([]);
let [generate, setGenerate] = useState(true);
const [search,setSearch] = useState<string>("");
const [allquotes, setAllquotes] = useState<ApiQuote[]>([]);
const [filterQuotes,setFilterQuotes] = useState<string[]>([]);


useEffect(()=>{
  setFilterQuotes((filterQuotes)=>{
    return filterQuotes.filter((quote)=> 
    quote.includes(filterQuotes[0]))
  })

},[filterQuotes]);

//  useEffect(()=>{
// setQuotes(allquotes)
// },[allquotes])
// let generate = false;
const onSearchChangeHandler = (e:any) => {
    setSearch(e.target.value);
  }


// const getNewQuote = (e:any) => {
//   fetch("https://api.quotable.io/quotes/random")
//   .then((response) => response.json())
//   .then((data:ApiQuote)=>{
//     setQuotes([...allquotes,data]);
//   });
// }  

function getNewQuote()
    {
      setGenerate(!generate)
    } 
// function addFilter(e:any) : void{
//   setFilterQuotes(e.target.innerHTML);
//   console.log("vl:",e.target);

//   console.log(filterQuotes);

// }    
    useEffect(()=>{
      fetch("https://api.quotable.io/quotes/random")
      .then((response) => response.json())
      .then((data:ApiQuote[])=>{
        setQuotes([data[0],...quotes]);
    });
    },[generate]);

  useEffect(()=>{
    fetch("https://api.quotable.io/quotes/random?limit=3")
    .then((response) => response.json())
    .then((data:ApiQuote[])=>{
      setQuotes(data);
    });
  },[]);

  return (
    <>
    {/* {JSON.stringify(quotes)} */}
    <button className='new-quote' onClick={getNewQuote}>New Quotes</button>
    <input className='filter-input' type="text" placeholder='Filter' onChange={onSearchChangeHandler} value={filterQuotes}/>
    {quotes.map((quote)=>{
      return <Quotes key = {quote._id} quote=
      {quote} filterQuotes={filterQuotes} setFilterQuotes={setFilterQuotes} />
    })}
    </>
  );
}

export default App;
