import { ApiQuote } from './types/quotes.types'
import './Quote.scss'
import App from './App'
interface QuoteProps{
    quote:ApiQuote
    filterquote: string[]
}
const Quotes = ({quote,filterquote}:QuoteProps) => {
  return (

    <div className='quote-container'>
        <h1 className='quote'>{quote.content}</h1>
        <p className='quote-author'>~{quote.author}</p>
        <p className='quote-date'>{quote.dateAdded}</p>

    <div className='quote-tag'>
        {quote.tags.map((q)=>{
            return <span className='quote-tag-single' onClick={addFilter} key={q}>{q}</span>
        })}
       </div>
    </div>

  );
}

export default Quotes
