import React, { useContext } from 'react';
import List from './List';
import { TodoContext } from './TodoList';
import './Main.scss';

// interface MidsectionProps {
//   searchTerm: string;
//   setSearchTerm: (value: string) => void;
//   newTodoText: string;
//   setNewTodoText: (value: string) => void;
//   todos: string[];
//   setTodos: (value: string[]) => void;
// }
// interface Todo {
//     id: number;
//     text: string;
//   }
interface ITodoMidContext{
    todos: string[];
    handleDeleteTodo: (index: number) => void;
}


export const TodoMidContext = React.createContext<ITodoMidContext>({
    todos:[],
    handleDeleteTodo: () => {}
  });



interface TodoMidProviderProps {
    children: React.ReactNode;
  }

  export function TodoMidProvider({ children }:TodoMidProviderProps){
    const {todos,setTodos} = useContext(TodoContext);
    const handleDeleteTodo = (index: number) => {
        const updatedTodos = [...todos];
        updatedTodos.splice(index, 1);
        setTodos(updatedTodos);
      };

    // const search =  (e:any) => {
    //         setSearchTerm(e.target.value)
    // };

return (
    <TodoMidContext.Provider value={{todos,handleDeleteTodo}} >
        {children}
        </TodoMidContext.Provider>
);
};


const Midsection = () => {
    const {searchTerm,newTodoText,setNewTodoText,todos,setTodos} = useContext(TodoContext);
  const handleAddTodo = () => {
    if (newTodoText.trim() !== '') {
      setTodos([...todos, newTodoText]);
      setNewTodoText('');
    }
  };


//   const handleDeleteTodo = (index: number) => {
//     const updatedTodos = [...todos];
//     updatedTodos.splice(index, 1);
//     setTodos(updatedTodos);
//   };

  const filteredTodos = searchTerm
    ? todos.filter((todo) => todo.toLowerCase().includes(searchTerm.toLowerCase()))
    : todos;

  return (
    <div id="Mid-section">
      <h2>Add Items</h2>
      <input
        id="Add-itembox"
        type="text"
        placeholder="Add new item"
        value={newTodoText}
        onChange={(e) => setNewTodoText(e.target.value)}
      />
      <button id="submitbtn" onClick={handleAddTodo}>
        Submit
      </button>
      <List />
    </div>
  );
}

export default Midsection;
